/** * Created by Hoshiko on 4/30/15. */public class Clarinet extends ReedInstrument {    boolean reed;    @Override    boolean hasReed(){        return reed;    }    @Override    public String play(){        return "reed sound";    }    @Override    void place_reed(){        this.reed = true;    }    @Override    void remove_reed(){        this.reed = false;    }}