package nyc.c4q.hoshikoo;/** * Created by Hoshiko on 4/30/15. */public class Card{    public String cardSign;    public int cardNumber;    public Card(){    }    public Card (String cardSign){        this.cardSign = cardSign;    }    public Card (String cardSign, int cardNumber){        this.cardSign = cardSign;        this.cardNumber = cardNumber;    }    public String getCardSign()    {        return cardSign;    }    public void setCardSign(String cardSign)    {        this.cardSign = cardSign;    }    public int getCardNumber()    {        return cardNumber;    }    public void setCardNumber(int cardNumber)    {        this.cardNumber = cardNumber;    }}