package nyc.c4q.hoshikoo;/** * Created by Hoshiko on 5/1/15. */public class Character {    String name;    int age;    public Character (String name, int age) {        this.name = name;        this.age = age;    }    public Character (String name) {        this.name = name;    }    public void setName(String newName) {        this.name = newName;    }    public String getName() {        return this.name;    }    public void setAge(int newAge) {        this.age = newAge;    }    public int getAge() {        return this.age;    }}