package nyc.c4q.hoshikoo;import java.util.ArrayList;import java.util.Collections;/** * Created by Hoshiko on 4/27/15. *///Create a class for a Domestic Animal, perhaps a DomesticCat,// that both inherits from Animal and implements the Domesitc interface.public class DomesticCat extends Animal implements Domestic{    String name;    String speciesName;    public DomesticCat(String speciesName)    {        super(speciesName);    }    //Create a constructor for your Domestic animal    // that takes in both the species name and given name.    public DomesticCat(String name, String speciesName){        super(speciesName);        this.name = name;    }    @Override    public void setName(String name)    {        this.name =name;    }    @Override    public String getName()    {        return name;    }    //Override the compareTo function for    // your Domestic animal so that it compares    // two such animals by their given name instead    // of their species name.    @Override    public int compareTo(Object o){        int equal = 0;        int after = 1;        int before = -1;        DomesticCat dCat =(DomesticCat) o;        String thisName = this.getName();        String oName = dCat.getName();        if(thisName.equals(oName)){            return equal;        }else {            ArrayList<String> list = new ArrayList<String>();            list.add(thisName);            list.add(oName);            Collections.sort(list);            if(list.get(0).equals(oName)){                return after;            }else{                return before;            }        }    }    //Implement equals to compare by given name.    @Override    public boolean equals(Object o) {        if(this.compareTo(o)==0){            return true;        }else{            return false;        }    }}