package nyc.c4q.hoshikoo;import java.util.ArrayList;import java.util.HashMap;/** * Created by Hoshiko on 4/30/15. */public class PlayingCards extends Game {    @Override    public ArrayList<String> getpieces(){        ArrayList<String> cardsMap = new ArrayList<String>();    for(int i = 1; i<14; i++){        cardsMap.add("Heart-"+i);        cardsMap.add("Diamond-"+i);        cardsMap.add("Club-"+i);        cardsMap.add("Spade-"+i);    }    return cardsMap;    }}