package nyc.c4q.hoshikoo;/** * Created by Hoshiko on 5/1/15. */public class Room {    String roomName;    int location;    public Room (String roomName, int location) {        this.roomName = roomName;        this.location = location;    }    public Room (String roomName) {        this.roomName = roomName;    }    public void setroomName(String newroomName) {        this.roomName = newroomName;    }    public String getroomName() {        return this.roomName;    }    public void setlocation(int newlocation) {        this.location = newlocation;    }    public int getlocation() {        return this.location;    }}