package nyc.c4q.hoshikoo;/** * Created by Hoshiko on 5/1/15. */public class Weapon {    String weaponName;    int power;    public Weapon (String weaponName, int power) {        this.weaponName = weaponName;        this.power = power;    }    public Weapon (String weaponName) {        this.weaponName = weaponName;    }    public void setWeaponName(String newweaponName) {        this.weaponName = newweaponName;    }    public String getWeaponName() {        return this.weaponName;    }    public void setPower(int newpower) {        this.power = newpower;    }    public int getPower() {        return this.power;    }}